from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Button, Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase

# Set up all devices.
prime_hub = PrimeHub(top_side=-Axis.X, front_side=Axis.Y)
motorEsquerda = Motor(Port.A, Direction.COUNTERCLOCKWISE)
motorDireita = Motor(Port.E, Direction.CLOCKWISE)
drive_base = DriveBase(motorEsquerda, motorDireita, 62, 94)

def andarReto(distancia):
    drive_base.straight(distancia)

def end():
    drive_base.stop()

def virarAngulo(anguloDesejado):
    drive_base.turn(anguloDesejado)

def pegarGuinada():
    return 0

def beep():
    prime_hub.speaker.beep(500, 100)

def resetarGuinada():
    prime_hub.imu.reset_heading(0)
    drive_base.reset(0, 0)


# The main program starts here.
prime_hub.system.set_stop_button(Button.BLUETOOTH)
motorEsquerda.control.limits(acceleration=10000)
motorEsquerda.control.limits(speed=2000)
motorDireita.control.limits(acceleration=10000)
motorDireita.control.limits(speed=2000)
drive_base.settings(straight_speed=1000)
drive_base.settings(straight_acceleration=200)
drive_base.settings(turn_acceleration=200)
drive_base.settings(turn_rate=500)
drive_base.use_gyro(True)
prime_hub.imu.reset_heading(0)
drive_base.reset(0, 0)
drive_base.stop()
