from pybricks.hubs import PrimeHub
from pybricks.parameters import Button, Color, Direction, Port, Stop
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
motorEsquerda = Motor(Port.E, Direction.COUNTERCLOCKWISE)
motorDireita = Motor(Port.A, Direction.CLOCKWISE)
robo = DriveBase(motorEsquerda, motorDireita, 62, 130)
garraDireita = Motor(Port.C, Direction.COUNTERCLOCKWISE)
garraEsquerda = Motor(Port.B, Direction.COUNTERCLOCKWISE)

# Initialize variables.
missao = 1

async def menu():
    global missao
    await wait(0)
    prime_hub.display.number(missao)
    garraDireita.control.limits(acceleration=2000)
    robo.settings(straight_speed=500)
    robo.settings(straight_acceleration=500)
    robo.settings(turn_rate=300)
    robo.settings(turn_acceleration=400)
    if Button.LEFT in prime_hub.buttons.pressed():
        await prime_hub.speaker.beep(880, 120)
        if missao > 1:
            missao = missao - 1
        await wait(200)
    if Button.RIGHT in prime_hub.buttons.pressed():
        await prime_hub.speaker.beep(880, 120)
        if missao < 9:
            missao = missao + 1
        await wait(200)
    if Button.CENTER in prime_hub.buttons.pressed():
        await prime_hub.speaker.beep(784, 120)
        await prime_hub.speaker.beep(988, 120)
        await prime_hub.speaker.beep(1175, 150)
        if missao == 1:
            await Missao1()
        elif missao == 2:
            await Missao2()
        elif missao == 3:
            await MIssao3()
        elif missao == 4:
            await Missao4()
        elif missao == 5:
            await Missao5()
        elif missao == 6:
            await Missao6()
        elif missao == 7:
            await Miss_C3_A3o_7()
        else:
            await Miss_C3_A3o_8()
        await prime_hub.speaker.beep(1047, 200)
        await wait(50)
        await prime_hub.speaker.beep(1319, 200)

async def testeBateria():
    await wait(0)
    if prime_hub.battery.voltage() >= 8200:
        prime_hub.light.on(Color.GREEN)
    elif 7500 <= prime_hub.battery.voltage() < 8200:
        prime_hub.light.on(Color.YELLOW)
    else:
        prime_hub.light.on(Color.RED)

async def Missao4():
    await wait(0)
    # Essa é a programação para fazer as missões 12, 15
    robo.settings(straight_acceleration=150)
    await robo.straight(580)
    await robo.straight(-50)
    await garraDireita.run_angle(-800, 700)
    robo.settings(straight_acceleration=800)
    await robo.straight(-500)
    robo.stop()
    await multitask(
        garraDireita.run_angle(800, 700),
    )

async def Missao5():
    await wait(0)
    robo.settings(straight_acceleration=750)
    # aqui o robô vira para ir para o outro lado fazendo a missão 10
    await robo.straight(400)
    robo.settings(turn_acceleration=150)
    await robo.turn(-45)
    robo.settings(turn_acceleration=1000)
    await robo.straight(420)
    robo.settings(turn_acceleration=150)
    await robo.turn(45)
    await robo.straight(310)
    await garraEsquerda.run_angle(4000, -1000)
    await garraEsquerda.run_angle(2000, 1000)
    await robo.turn(30)
    robo.settings(straight_speed=1000)
    await robo.straight(650)
    robo.stop()

async def Missao6():
    await wait(0)
    # Essa é a programação para fazer as missões 5, 6 e 7
    await robo.straight(245)
    await robo.turn(90)
    await robo.straight(450)
    await robo.turn(50)
    await robo.straight(60)
    await garraEsquerda.run_angle(200, -420)
    # aqui ele abaixa a garra para pegar o objeto e depois vai pra frente
    await robo.turn(1)
    await robo.straight(80)
    await garraEsquerda.run_angle(300, 400)
    await robo.straight(-20)
    robo.settings(turn_acceleration=600)
    await robo.turn(-45)
    await robo.straight(-55)
    robo.settings(turn_acceleration=500)
    await robo.turn(-15)
    # Aqui ele vira antes de ir pra frente pra fazer a missão 5
    await robo.straight(113)
    robo.settings(turn_acceleration=700)
    await robo.turn(-35)
    await robo.turn(40)
    # Aqui o robô volta para base
    robo.settings(straight_acceleration=1000)
    await robo.straight(-700)
    robo.stop()

async def subtask():
    robo.settings(straight_acceleration=400)
    await robo.straight(-60)

async def Missao1():
    await wait(0)
    # Essa é a programação para fazer as missões 1 e 2
    robo.settings(straight_acceleration=900)
    await robo.straight(648)
    await robo.straight(-155)
    # aqui o Robô levanta a garra
    await garraEsquerda.run_angle(800, -300)
    await robo.straight(65)
    await garraEsquerda.run_angle(800, 450)
    await garraEsquerda.run_angle(300, -600)
    # aqui o Robô levanta a garra e vai pra frente para fazer a missão 2
    await robo.straight(-115)
    await robo.turn(45)
    robo.settings(straight_acceleration=800)
    await robo.straight(240)
    robo.settings(turn_acceleration=200)
    await robo.turn(-90)
    # aqui o robô vira para fazer a missão 2
    robo.settings(straight_acceleration=100)
    await robo.straight(240)
    robo.settings(turn_acceleration=200)
    await robo.turn(10)
    # aqui o robô vai para tras antes de levantar a garra
    await multitask(
        subtask(),
        garraDireita.run_angle(200, 400),
    )
    await robo.turn(-18)
    robo.settings(straight_acceleration=800)
    await robo.straight(-150)
    robo.settings(turn_acceleration=400)
    await robo.turn(60)
    robo.settings(straight_acceleration=1000)
    await robo.straight(-800)
    robo.stop()

async def MIssao3():
    await wait(0)
    # Essa é a programação para fazer a missão 15 (1/3)
    robo.settings(straight_acceleration=1000)
    await robo.straight(635)
    await robo.turn(10)
    robo.settings(straight_speed=1000)
    await robo.straight(-690)
    await robo.turn(20)
    await wait(1000)
    # aqui ele faz a missão 14 (3/7)
    robo.settings(straight_acceleration=1000)
    await robo.straight(480)
    robo.settings(straight_speed=1000)
    await robo.straight(-600)
    robo.stop()

async def subtask2():
    robo.settings(straight_acceleration=1000)
    await robo.straight(-890)

async def Missao2():
    await wait(0)
    # Essa é a programação para fazer as missões 3 e 4 e 13
    await multitask(
        garraEsquerda.run_angle(600, 275),
        subtask2(),
    )
    robo.settings(straight_acceleration=100)
    await robo.straight(100)
    robo.settings(turn_acceleration=150)
    await robo.turn(-84)
    await robo.straight(153)
    # aqui o robô faz as duas missões ao mesmo tempo
    await multitask(
        garraEsquerda.run_angle(600, 600),
        garraDireita.run_angle(600, -350),
    )
    await robo.straight(-130)
    await garraEsquerda.run_angle(800, 400)
    robo.settings(turn_rate=30)
    # o robô vira pra fazer a missão 13
    await robo.turn(35)
    robo.settings(straight_acceleration=100)
    await robo.straight(150, then=Stop.NONE)
    await garraDireita.run_angle(600, 350)
    robo.settings(straight_acceleration=100)
    await robo.straight(78, then=Stop.NONE)
    await robo.turn(-35)
    robo.settings(turn_acceleration=70)
    await robo.turn(25)
    robo.settings(straight_acceleration=150)
    await robo.straight(-280)
    # robô vira para voltar para a base
    await robo.turn(70)
    robo.settings(straight_acceleration=1000)
    await robo.straight(800)
    robo.settings(turn_rate=500)
    robo.stop()

async def Miss_C3_A3o_7():
    await wait(0)
    # Essa é a programação para fazer as missões 9
    robo.settings(straight_speed=600)
    await robo.straight(430)
    robo.settings(turn_acceleration=500)
    await robo.turn(-45)
    # abaixa a garra e levanta o telhando
    robo.settings(straight_acceleration=250)
    await robo.straight(150)
    await garraEsquerda.run_angle(1000, 490)
    robo.settings(straight_acceleration=500)
    await multitask(
        robo.straight(-160),
        garraDireita.run_angle(1000, 550),
    )
    await wait(200)
    await robo.straight(50)
    await garraEsquerda.run_angle(200, -300)
    robo.settings(straight_acceleration=1000)
    await robo.straight(-450)
    robo.stop()

async def Miss_C3_A3o_8():
    await wait(0)

async def main():
    prime_hub.system.set_stop_button(Button.BLUETOOTH)
    await testeBateria()
    await wait(500)
    robo.use_gyro(True)
    await prime_hub.speaker.beep(880, 120)
    await wait(40)
    await prime_hub.speaker.beep(1320, 120)
    await wait(40)
    await prime_hub.speaker.beep(1760, 180)
    while True:
        await wait(0)
        await menu()


run_task(main())